import logo from './logo.svg';
import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      todoList:[],
      activeItem:{
        id:null,
        title:'',
        description:'',
        completed:false,
      },
      editing:false,
    }
  };

  render() {

    return(
      <div className="container">

        <div id='task-container'>
          <div id='forms'>
            <form id='form'>
              <div className='flex-wrapper'>
                <div style={{flex: 4}}>
                  <input className='form-control' id='title' type='text' name='title' placeholder='enter your task' />
                </div>
                <div style={{flex: 1}}>
                  <input className='btn' id='submit' type='submit' name='add' />
                </div>

              </div>
            </form>
          </div>
          <div id='lists'>

          </div>
        </div>

      </div>
    )
  }
}

export default App;
