import logo from './logo.svg';
import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      todoList:[],
      activeItem:{
        id:null,
        title:'',
        description:'',
        completed:false,
      },
      editing:false,
    }
    this.fetchTasks =this.fetchTasks.bind(this)
    this.handleChange =this.handleChange.bind(this)
    this.handleSubmit =this.handleSubmit.bind(this)

  };
  componentWillMount(){
    this.fetchTasks()
  }
  fetchTasks(){
    console.log('Fetching....')
    fetch('http://127.0.0.1:8000/api/task-list/')
    .then(response => response.json())
    .then(data =>
      this.setState({
        todoList : data
      })
      )
  }
  handleChange(e){
    var name= e.target.name;
    var value= e.target.value;
    console.log('Name:', name)
    console.log('Value:', value)
    this.setState({
      activeItem:{
        ...this.state.activeItem,
        title:value
      }

    })
  }
  handleSubmit(e){
    e.preventDefault()
    console.log('ITEM:', this.state.activeItem)
    var url = 'http://127.0.0.1:8000/api/task-create/'
    fetch(url, {
      method:"POST",
      headers:{
        'Content-Type':'application/json',
      }
    })
  }

  render() {
    var tasks=this.state.todoList
    return(
      <div className="container">

        <div id='task-container'>
          <div id='forms'>
            <form id='form' onSubmit={this.handleSubmit}>
              <div className='flex-wrapper'>
                <div style={{flex: 4}}>
                  <input onChange={this.handleChange} className='form-control' id='title' type='text' name='title' placeholder='enter your task' />
                </div>
                <div style={{flex: 1}}>
                  <input className='btn' id='submit' type='submit' name='add' />
                </div>

              </div>
            </form>
          </div>
          <div id='lists'>
              {tasks.map(function(task, index){
                return(
                  <div key={index} className='task-wrapper flex-wrapper'>
                    <div style={{flex:7}}>   
                      <span>{task.title}</span>
                    </div>
                    <div style={{flex:1}}>   
                      <button className='btn btn-sm btn-outline-dark'>Edit</button>
                    </div>
                    <div style={{flex:1}}>   
                    <button className='btn btn-sm btn-outline-dark'>-</button>
                    </div>
                  </div>
                )
              })}
          </div>
        </div>

      </div>
    )
  }
}

export default App;
