import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      todoList:[],
      activeItem:{
        id:null,
        title:'',
        description:'',
        completed:false,
      },
      editing:false,
    }
    this.Listing =this.Listing.bind(this)
    this.handleChange =this.handleChange.bind(this)
    this.Creation =this.Creation.bind(this)
    // this.getCookie =this.getCookie.bind(this)

  };
//   getCookie(name) {
//     let cookieValue = null;
//     if (document.cookie && document.cookie !== '') {
//         const cookies = document.cookie.split(';');
//         for (let i = 0; i < cookies.length; i++) {
//             const cookie = cookies[i].trim();
//             // Does this cookie string begin with the name we want?
//             if (cookie.substring(0, name.length + 1) === (name + '=')) {
//                 cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
//                 break;
//             }
//         }
//     }
//     return cookieValue;
// }

  componentWillMount(){
    this.Listing()
  }
// Task list display
  Listing(){
    console.log('Fetching....')
    fetch('http://127.0.0.1:8000/api/task-list/')
    .then(response => response.json())
    .then(data =>
      this.setState({
        todoList : data
      })
      )
  }
  handleChange(e){
    var name= e.target.name;
    var value= e.target.value;
    console.log('Name:', name)
    console.log('Value:', value)
    this.setState({
      activeItem:{
        ...this.state.activeItem,
        title:value
      }

    })
  }
  // Task creation
  Creation(e){
    e.preventDefault()
    console.log('ITEM:', this.state.activeItem)
    // var csrftoken = this.getCookie('csrftoken');
    var url = 'http://127.0.0.1:8000/api/task-create/'
    if(this.state.editing == true){
      url='http://127.0.0.1:8000/api/task-update/'+this.state.activeItem.id
      // url='http://127.0.0.1:8000/api/task-update/{{updateId}/'
      this.setState({
        editing: false
      })

    }


    fetch(url, {
      method:"POST",
      headers:{
        'Content-Type':'application/json',
        'mode' : 'n0-cors'
      },
      body:JSON.stringify(this.state.activeItem)
    }).then((response) => {
      this.Listing()
      this.setState({
        activeItem:{
          id:null,
          title:'',
          description:'',
          completed:false,
        }
      })
    }).catch(function(error)
    {
      console.log('ERROR:', error)
    })  
  }
  // Task edit
  startEditing(task){
    this.setState({
      activeItem:task,
      editing:true,
    })
  }
  render() {
    var tasks=this.state.todoList
    var self=this
    return(
      <div className="container">
        <div id='task-container'>
        <h1 id='h1'> TODO LIST</h1>
          <div id='forms'>
            <form id='form' onSubmit={this.Creation}>
              <div className='flex-wrapper'>
                <div style={{flex: 4}}>
                  <input onChange={this.handleChange} className='form-control' value={this.state.activeItem.title} id='title' type='text' name='title' placeholder='enter your task' />
                </div>
                <div style={{flex: 1}}>
                  <input className='btn' id='submit' type='submit' name='add' />
                </div>

              </div>
            </form>
          </div>
          <div id='lists'>
              {tasks.map(function(task, index){
                return(
                  <div key={index} className='task-wrapper flex-wrapper'>
                    <div style={{flex:7}}>   
                      <span>{task.title}</span>
                    </div>
                    <div style={{flex:1}}>   
                      <button onClick={() => self.startEditing(task)} className='btn btn-sm btn-outline-dark'>Edit</button>
                    </div>
                    <div style={{flex:1}}>   
                    <button className='btn btn-sm btn-outline-dark'>-</button>
                    </div>
                  </div>
                )
              })}
          </div>
        </div>

      </div>
    )
  }
}

export default App;
